mapper.select:
/* com.neofect.gts.services.common.repository.CodeRepository.getSmCommonCodeList */
 	SELECT 
 		A.DCODE AS CODE,
        A.DNAME AS LABEL
    FROM 
    	SM_COMCODE_D A
    WHERE 
       		A.SYS_ID = #{sysId}
         	AND A.HCODE = #{hcode}
         	AND A.USE_YN = #{useYn}
       ORDER BY A.DVALUE, A.DCODE

	/* com.neofect.gts.services.common.repository.CodeRepository.getSmCommonCodeListByDvalue2 */
		SELECT 
		DVALUE2 AS CODE
           , DNAME AS LABEL
        FROM SM_COMCODE_D
        WHERE SYS_ID = #{sysId}
         AND HCODE = #{hcode}
       ORDER BY CODE

	/* com.neofect.gts.services.common.repository.CodeRepository.getSmCommonCodeListDW */
		SELECT 
			 A.DCODE AS CODE,
             A.DNAME AS LABEL
        FROM 
        	SM_COMCODE_D A
        WHERE A.SYS_ID = #{sysId}
         AND A.HCODE = #{hcode}
         AND A.USE_YN LIKE #{useYn}
         AND NVL(A.RELATE_CODE1,'%') LIKE #{relateCode1}
         AND NVL(A.RELATE_CODE2,'%') LIKE #{relateCode2}
         AND NVL(A.RELATE_CODE3,'%') LIKE #{relateCode3}
       ORDER BY A.DVALUE, A.DCODE
