mapper.select:
/* com.neofect.gts.services.worker.worker.repository.WorkerInfoRepository.selectWorkerCheckRegistNumber */
SELECT	A.MEMBER_ID,
		A.NAME,
		B.DEPT_NAME
FROM 
	CODE_MEMBER A
	LEFT OUTER JOIN	CODE_DEPT B	ON A.DEPT_ID = B.DEPT_ID
WHERE 
	X_DECRYPT(A.RRN_NO) =	#{rrnNo}
	AND	A.MEMBER_ID	!= #{memberId}
